class AttendeesScreen extends StatefulWidget {
  static const String id = 'attendees_screen';

  const AttendeesScreen({Key? key}) : super(key: key);

  @override
  _AttendeesScreenState createState() => _AttendeesScreenState();
}

class _AttendeesScreenState extends State<AttendeesScreen> {
  List<AppUser> _users = [];
  @override
  void initState() {
    super.initState();
    _setupAttendees();
  }

  _setupAttendees() async {
    String currentUserId =
        Provider.of<UserData>(context, listen: false).currentUserId!;
    List<AppUser> users =
        await Provider.of<DatabaseService>(context, listen: false)
            .getAllUsers(currentUserId);
    if (mounted) {
      setState(() {
        _users = users;
      });
    }
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: Theme.of(context).primaryColor,
      appBar: AppBar(
        backgroundColor:
            AppConstants.hexToColor(AppConstants.APP_PRIMARY_COLOR),
        title: const Text(
          'Attendees',
          style: TextStyle(
            fontSize: 28.0,
            fontWeight: FontWeight.bold,
          ),
        ),
        elevation: 0.0,
        actions: <Widget>[
          IconButton(
            icon: const Icon(Icons.exit_to_app),
            onPressed: Provider.of<AuthService>(context, listen: false).logout,
          ),
        ],
      ),
      body: Column(
        children: <Widget>[
          AllAttendees(appUsers: _users),
        ],
      ),
    );
  }
}

